defined('BASEPATH') OR exit('No direct script access alloweb');
use Google\Client as GoogleClient;
use Google\Service\Oauth2;


class Login extends CI_Controller{

    public function __construct(){

        parent::__construct();
        $this->load->database();
        $this->load->model('Login_model');
        $this->load->helper('url');
		$this->load->library('session');
    }

    public function google_login(){

        
		$client   = new GoogleClient();
		$client->setApplicationName('ecommerce store');
		$client->setClientId('707076342730-as3u14itiveclv1ncuet4g4pccpbuv4l.apps.googleusercontent.com');
		$client->setClientSecret('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');
		$client->setRedirectUri('http://localhost/ci3/Login/google_login');
		$client->addScope(['https://www.googleapis.com/auth/userinfo.email', 'https://www.googleapis.com/auth/userinfo.profile']);


		if ($code = $this->input->get('code', true)) {
			
			$token = $client->fetchAccessTokenWithAuthCode($code);
			$client->setAccessToken($token);
			$oauth = new OAuth2($client);
			$user_info = $oauth->userinfo->get();
			
			$data['name'] 	 	     = $user_info->name;
			$data['email_address'] 	 = $user_info->email;
			$data['profile_picture'] = $user_info->picture;

			$_SESSION['access_token'] = $code;
            $_SESSION['email']        = $data['email_address'];

			
				if($user['userData'] = $this->Login_model->getUser($data['email_address'])){

				 	$this->session->set_userdata('user',$user['userData']['email_address']);

					if ($user['userData']['profile_picture'] == null) {

						$this->Login_model->updateRegister($data);
						
					}
				

				}else{

					$this->Login_model->createUser($data);

				}
				
				redirect('index');

		    }else{
				

				$url = $client->createAuthUrl();
			
				 header('Location:'.filter_var($url, FILTER_SANITIZE_URL));
		}
    }

}


